curl -s 'https://api.datacite.org/graphql' -H 'Accept-Encoding: gzip, deflate, br' -H 'Content-Type: application/json' -H 'Accept: application/json' -H 'Connection: keep-alive' -H 'DNT: 1' -H 'Origin: https://api.datacite.org' --data-binary '{"query":"query {  organization(id: \"ror.org/01rhff309\") {    datasets {      totalCount     nodes { id }}}}"}' --compressed | grep -zoe '"id":\s*\K[^\s,]*(?=\s*,)'
